import "./ContactFormStyles.css";
import { useRef } from "react";
import emailjs from "@emailjs/browser";

const ContactForm = () => {
    alert("")
    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();

        // Validate form fields before sending the email
        if (validateForm()) {
            emailjs.sendForm('Codexsuvro_Abhinav Travelo', 'template_4n4q4m7', form.current, 'GzNR0Q0KxhyQ02uB7')
                .then((result) => {
                    console.log(result.text);
                    e.target.reset();
                }, (error) => {
                    console.log(error.text);
                });
        }
    };

    const validateForm = () => {
        // Simple validation for demonstration purposes
        const nameInput = form.current.querySelector('input[name="to_name"]');
        const emailInput = form.current.querySelector('input[name="from_name"]');
        const messageInput = form.current.querySelector('textarea[name="message"]');

        if (!nameInput.value.trim()) {
            alert('Please enter your name.');
            return false;
        }

        if (!emailInput.value.trim() || !isValidEmail(emailInput.value)) {
            alert('Please enter a valid email address.');
            return false;
        }

        if (!messageInput.value.trim()) {
            alert('Please enter a message.');
            return false;
        }

        return true;
    };

    const isValidEmail = (email) => {
        // Simple email validation for demonstration purposes
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(email);
    };

    return (
        <div className="form-container">
            <h1>Send a message to us!</h1>
            <form ref={form} onSubmit={sendEmail}>
                <input placeholder="Name" name="to_name" type="text" />
                <input placeholder="Email" type="email" name="from_name" />
                <textarea placeholder="Message" rows="4" name="message"></textarea>
                <button type="submit" value="Send">Send Message</button>
            </form>
        </div>
    );
};


export default ContactForm;
